    private void jbtnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnDeleteActionPerformed
        if (!jtfVisor.getText().equals("0")) {

            jtfVisor.setText(jtfVisor.getText().substring(0, jtfVisor.getText().length() - 1));

            if (jtfVisor.getText().isEmpty()) {
                jtfVisor.setText("0");
            }
        }
    }//GEN-LAST:event_jbtnDeleteActionPerformed

    private void jbtnPositivoNegativoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnPositivoNegativoActionPerformed
        if ((Double.valueOf(jtfVisor.getText())) > 0) {
            jtfVisor.setText("-" + jtfVisor.getText());
        }
        else {
            jtfVisor.setText(jtfVisor.getText().replace("-", ""));
        }
    }//GEN-LAST:event_jbtnPositivoNegativoActionPerformed

    private void jbtnRaizQuadradaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnRaizQuadradaActionPerformed
        if (Double.valueOf(jtfVisor.getText()) >= 0) {
            jtfVisor.setText("" + Math.sqrt(Double.valueOf(jtfVisor.getText())));
            limparVisorAuto = true;
        }
        else {
            JOptionPane.showMessageDialog(null, "RAIZ NÃO POSSÍVEL", "ATENÇÃO", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jbtnRaizQuadradaActionPerformed

