    private void armazenar(Character c) {
        
        if(n1 == 0d){
            opc = c;
            n1 = Double.parseDouble(jtfVisor.getText());
            jtfVisor.setText("0");
        }
        else
        if(n2 == 0d){
            opc = c;
            n2 = Double.parseDouble(jtfVisor.getText());
            jtfVisor.setText("0");
        }
        else{
            calcular();
            armazenar(c);
        }
    }

    private void calcular() {
        switch (opc) {
            case '+':
                n1 = n1 + n2;
            break;
            
            case '/':
                n1 = n1 / n2;
            break;
            
            case '-':
                n1 = n1 - n2;
            break;
            
            case '*':
                n1 = n1 * n2;
            break;
        }
        
        n2 = 0d;
    }
    
    private void limparVisor(){
        
        if(!limparVisorAuto){
            return;
        }
        else{
            jtfVisor.setText("0");
        }
        
        limparVisorAuto = false;
    }

    private void verResultado() {
        
        if(n1 != 0 && n2 != 0){
            calcular();
        }
        
        n2 = Double.parseDouble(jtfVisor.getText());
        calcular();
        
        jtfVisor.setText(n1+"");
        limparVisorAuto = true;
        n1 = 0d;
        n2 = 0d;
        opc = '+';
    }
    

